import React, { useState, useEffect } from 'react';
import BookCard from './BookCard';

const BookSearch = ({ toggleBookshelf, bookshelf }) => {
  const [query, setQuery] = useState('');
  const [books, setBooks] = useState([]);

  const fetchBooks = async (searchQuery) => {
    if (searchQuery.length > 2) {
      try {
        const response = await fetch(`https://openlibrary.org/search.json?q=${searchQuery}&limit=10&page=1`);
        const data = await response.json();
        setBooks(data.docs);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    } else {
      setBooks([]);
    }
  };

  useEffect(() => {
    fetchBooks(query);
  }, [query]);

  const isBookInBookshelf = (book) => {
    return bookshelf.some((b) => b.key === book.key);
  };

  return (
    <div>
      <input type="text" value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Search for books..." />
      <div className="book-list">
        {books.map((book) => (
          <BookCard 
            key={book.key} 
            book={book} 
            toggleBookshelf={toggleBookshelf} 
            isBookInBookshelf={isBookInBookshelf} 
          />
        ))}
      </div>
    </div>
  );
};

export default BookSearch;
