import React, { useState, useEffect, useCallback } from 'react';
import BookCard from './BookCard';

const BookSearch = ({ addToBookshelf, bookshelf }) => {
  const [query, setQuery] = useState('');
  const [books, setBooks] = useState([]);

  const debounce = (func, wait) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => func(...args), wait);
    };
  };

  const fetchBooks = async (searchQuery) => {
    const response = await fetch(`https://openlibrary.org/search.json?q=${searchQuery}&limit=10&page=1`);
    const data = await response.json();
    setBooks(data.docs);
  };

  const debouncedFetchBooks = useCallback(debounce(fetchBooks, 500), []);

  useEffect(() => {
    if (query.length > 2) {
      debouncedFetchBooks(query);
    } else {
      setBooks([]);
    }
  }, [query, debouncedFetchBooks]);

  const isBookInBookshelf = (book) => {
    return bookshelf.some((b) => b.key === book.key);
  };

  return (
    <div>
      <input type="text" value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Search for books..." />
      <div className="book-list">
        {books.map((book) => (
          <BookCard 
            key={book.key} 
            book={book} 
            addToBookshelf={addToBookshelf} 
            isBookInBookshelf={isBookInBookshelf} 
          />
        ))}
      </div>
    </div>
  );
};

export default BookSearch;
